@Path("/")
public class MyResource {

   @GET
   public String get() { return "hello world"; }


   public static void main(String[] args) throws Exception 
   {
      TJWSEmbeddedJaxrsServer tjws = new TJWSEmbeddedJaxrsServer();
      tjws.setPort(8081);
      tjws.getDeployment().getActualResourceClasses().add(MyResource.class);
      tjws.start();
   }
}

public static void main(String[] args) throws Exception 
{
   final TJWSEmbeddedJaxrsServer tjws = new TJWSEmbeddedJaxrsServer();
   tjws.setPort(8081);

   tjws.start();
   org.resteasy.plugins.server.servlet.SpringBeanProcessor processor = new SpringBeanProcessor(tjws.getDeployment().getRegistry(), tjws.getDeployment().getFactory();
   ConfigurableBeanFactory factory = new XmlBeanFactory(...);
   factory.addBeanPostProcessor(processor);
}
